package edward_dunn.unit_test_examples;

public class UnitsConverter implements ConverterService {

	public double convertGramsToKG(int grams) {
		// TODO Auto-generated method stub
		return grams / 1000;
	}

	public double convertPoundsToStone(int pounds) {
		// TODO Auto-generated method stub
		return pounds / 14;
	}

	public double convertFeetToYards(int feet) {
		// TODO Auto-generated method stub
		return feet / 3;
	}

}
